import java.util.ArrayList;
import java.util.List;

public class ArrayTransformation {
    static int MOD = 1_000_000_007;
    public static void main(String[] args) {
        String s = "";
        int t = 492153482;
        List<Integer> nums = new ArrayList<>();
        int[] n = {23,20,4,11,4,24,13,25,12,21,17,7,6,21,12,11,22,25,22,16,19,8,16,18,19,16};
        for(int num : n){
            nums.add(num);
        }
        int result = lengthAfterTransformations(s,t,nums);
        System.out.println(result);
    }
    public static int lengthAfterTransformations(String s, int t, List<Integer> nums) {
        long start = System.nanoTime();
        int[] freq = new int[26];
        for (char c : s.toCharArray()) {
            freq[c - 'a']++;
        }
        int count = 0;
        for (int i = 0; i < t; i++) {
            int[] next = new int[26];
            for (int j = 0; j < 26; j++) {
                if (freq[j] > 0) {
                    int num = nums.get(j);
                    for (int k = 0; k < num; k++) {
                        next[(j + k + 1) % 26] = (next[(j + k + 1) % 26] + freq[j]) % MOD;
                    }
                }
            }
            freq = next;
        }
        for (int n : freq) {
            count = (count + n) % MOD;
        }
        long end = System.nanoTime();
        System.out.println("Completed in "+ (end-start) +"nanosecs");
        return count;

    }
}
